const StyledFooter = styled.footer`
  background: var(--black);
`

const FooterInner = styled.div`
  color: var(--white);
  padding: 60px 0;
  display: flex;
  justify-content: space-between;
  gap: 60px;
  a {
    color: var(--white);
    text-decoration: none;
    font-size: 0.9rem;
    display: inline-block;
    position: relative;
    :hover {
      .underline-svg {
        opacity: 1;
      }
    }
    .underline-svg {
      position: absolute;
      left: 0;
      top: 75%;
      opacity: 0;
      transition: 0.3s ease opacity;
      max-width: 100%;
    }
  }
  .contact {
    flex: 2;
  }
  .footer-menu {
    flex: 2;
    .footer-menu-inner {
      display: flex;
      flex-wrap: wrap;
      gap: 30px;
      a {
        flex: 1;
        flex-basis: 40%;
      }
    }
  }
  .social-links {
    flex: 2;
  }
`

const SmallPrint = styled.div`
  .line {
    display: block;
    height: 1px;
    background: var(--white);
    width: 100%;
  }
  div {
    text-align: right;
    color: var(--white);
    padding: 30px 0 60px 0;
  }
`

